INSERT INTO "public"."catalog" ("id", "name", "type_id", "image", "description", "list_title", "item_list", "description2", "list_title2", "item_list2", "underline", "category_id") VALUES (10, 'Материалы для косметологии', 2, '/images/lines/cosmetic.jpg', 'Материалы из&nbsp;Китая для проведения косметологических процедур.
