INSERT INTO "public"."catalog" ("id", "name", "type_id", "image", "description", "list_title", "item_list", "description2", "list_title2", "item_list2", "underline", "category_id") VALUES (12, 'Оборудование для производства катетеров', 2, '/images/lines/catheter.jpg', 'Российский рынок медицинских изделий, год от&nbsp;года, постепенно показывает растущее число отечественных компаний производящих широкий ассортимент изделий медицинского назначения. Многие из&nbsp;таких изделий, производятся уже внутри России.
