INSERT INTO "public"."catalog" ("id", "name", "type_id", "image", "description", "list_title", "item_list", "description2", "list_title2", "item_list2", "underline", "category_id") VALUES (75, 'Экструдерная линия для производства спирально витой трубы большого диаметра', 4, '/images/lines/tr.jpg', 'Спиральновитые трубы большого диаметра из&nbsp;полиэтилена используется при строительстве канализационных систем, ливневых стоков, различных очистных сооружений а&nbsp;так&nbsp;же для ремонта старых, изношенных трубопроводов.
