INSERT INTO "public"."catalog" ("id", "name", "type_id", "image", "description", "list_title", "item_list", "description2", "list_title2", "item_list2", "underline", "category_id") VALUES (97, 'Оборудование производства сухого гранулированного корма для рыб', 5, '/images/other/feed.jpg', 'Очень перспективное направление бизнеса. Отмечается недостаточное количество производственных мощностей в&nbsp;России для производства корма для аквакультур.
